class SuperMath{check(e){return this.isValidOperator(e.znak)?this.calculate(e.X,e.Y,e.znak):NaN}input(){return{X:parseFloat(prompt("Enter value for X:")),Y:parseFloat(prompt("Enter value for Y:")),znak:prompt("Enter an operator (+ - / * %):")}}isValidOperator(e){return/^[+\-*/%]$/.test(e)}calculate(e,t,r){switch(r){case"+":return e+t;case"-":return e-t;case"/":return e/t;case"*":return e*t;case"%":return e%t;default:return NaN}}}document.getElementById("runSuperMath").addEventListener("click",(function(){const e=new SuperMath,t=e.input(),r=e.check(t);isNaN(r)?document.getElementById("result").innerText="Invalid operator entered.":document.getElementById("result").innerText=`Result of the operation: ${r}`,document.getElementById("superMathModal").style.display="block"})),document.getElementById("closeSuperMathModal").addEventListener("click",(function(){document.getElementById("superMathModal").style.display="none"}));