function getWeather(e){const t=`https://api.openweathermap.org/data/2.5/weather?q=${e}&units=metric&APPID=5d066958a60d315387d9492393935c19`;$.ajax({url:t,type:"GET",dataType:"json",success:function(e){const t=document.getElementById("city"),n=document.getElementById("temperature"),o=document.getElementById("pressure"),d=document.getElementById("description"),m=document.getElementById("humidity"),i=document.getElementById("wind-speed"),c=document.getElementById("wind-direction"),r=document.getElementById("weather-icon");t.textContent=e.name,n.textContent=e.main.temp,o.textContent=e.main.pressure,d.textContent=e.weather[0].description,m.textContent=e.main.humidity,i.textContent=e.wind.speed,c.textContent=e.wind.deg,r.src=`https://openweathermap.org/img/w/${e.weather[0].icon}.png`},error:function(e){console.log("Помилка отримання погоди:",e)}})}getWeather("LVIV");